package NumberPrograms;

public class SumOfNaturalNumbers {
	// 1+2+3+4+-----+100= 5050
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=100;
		int sum=0;
		
		for(int i=1; i<=100;i++){
			sum=sum+i;
			
		}
		System.out.println(sum);
		
		// using while loop alos can 
		

	}

}
